import './App.css';
import axios from 'axios';
import Company from './Components/Company';
import Chef from './Components/Chef'

export default class App extends Component {
  state = {
    chef: {
      name: "Tom Nijam",
      title: "Exec Chef of KFC",
      profileUrl: "https://d1qb2nb5cznatu.cloudfront.net/users/8627618-medium_jpg?1543043897",
      comment:"\"Without the doubt, the best chicken you ever eat\"",
      url: "https://res.cloudinary.com/hksqkdlah/image/upload/s--C9EcOChh--/c_scale,dpr_2.0,f_auto,h_172,q_auto:low,w_172/34448_sfs-north-carolina-dipped-fried-chicken-18"
  }
  }

  // componentDidMount() {
  //   //const KEYWORD = "cat";
  //   axios.get(`https://source.unsplash.com/1600x900/?nature,water`)
  //     .then(res => {
  //       const chef = res.data;
  //       this.setState({ chef });
  //     })
  // }

  render() {
    return (
      <div className="flex-container-row all-container">
        <Chef className="flex-item-row"
        name= {this.state.chef.name}
        title= {this.state.chef.title}
        profileUrl= {this.state.chef.profileUrl}
        comment={this.state.chef.comment}
        url={this.state.chef.url}   
        />
        <Company className="flex-item-row" />
      </div>
    )
  }
}
