  //     .then(res => {
  //       const chef = res.data;
  //       this.setState({ chef });
  //     })
  // }
  render() {
    return (
      <div className="Row">
        <Chef
        name= {this.state.chef.name}
        title= {this.state.chef.title}
        profileUrl= {this.state.chef.profileUrl}
        comment={this.state.chef.comment}
        url={this.state.chef.url}   
        />
        <Company/>
      </div>
    )
  }
}
