import React, { Component } from 'react';
import classNames from 'classnames';

export default class Chef extends Component {

    render(){
        const { profileUrl, name, title, comment, url, className, website } = this.props;
        const wrapperCls = classNames( 'chef-container flex-container-col', className );

        return (

            <div className={wrapperCls}>
                <div className="expert-wrapper">                   
                    <img className="profile-pic" src={profileUrl} alt={name} width="75" height="75"/>                    
                    <div className="expert-info-card">
                        <strong className="bold-text">{name}</strong>

                        <div className="export-info">
                        Head Chef of 
                        <span className="venue text-span-2"> Smith &amp; Daughters</span>
                        </div>
                    </div>

                </div>
                <div className="comment-container flex-item-col">
                    <h2 className="h2">
                        {comment}
                    </h2>

                </div>
                <a href={website} target="_blank" className="pic-container">
                    <img src={url} alt={comment}  className="pic"/>
                </a>
            </div>
        );
    }
}
