import React, { Component } from 'react';
import classNames from 'classnames';

export default class Chef extends Component {

    render(){
        const { profileUrl, name, comment, url, className, website, venue } = this.props;
        const wrapperCls = classNames( 'chef-container', className );

        return (

            <div className={wrapperCls}>
                <div className="expert-wrapper">                   
                    <img className="profile-pic" src={profileUrl} alt={name} width="100" height="75"/>                    
                    <div className="expert-info-card">
                        <div className="expert-info name">
                        <strong className="bold-text">{name}</strong>
                        </div>
                        <div className="expert-info">
                        Head Chef of  
                        <span className="venue text-span-2"> {venue}</span>
                        </div>
                    </div>

                </div>
                <div className="recommendation-wrapper">
                    <div className="recommended-venue">recommends 
                    <span className="recommended-name text-span-3"> {venue}</span>
                    </div>
                    <p className="comment">
                        "{comment}"
                    </p>
                    <a href={website} target="_blank" className="pic-container">
                    <img src={url} alt={comment}  className="food-image"/>
                    </a>

                </div>

            </div>
        );
    }
}
